mapper.select:
select comment.*, film.name as filmName,user.name as userName from comment
        left join film on comment.film_id=film.id
        left join user on comment.user_id=user.id

    and film.name like concat('%',#{filmName},'%')
    and comment.user_id=#{userId}
    and comment.type=#{type}
    and comment.film_id=#{filmId}


order by comment.id desc

mapper.update:
UPDATE comment
SET
  sentiment     = #{sentiment},
  positive_prob = #{positiveProb},
  negative_prob = #{negativeProb}
WHERE id = #{id}
